CREATE TABLE games (rom_id INTEGER PRIMARY KEY, filename TEXT, filename_archive TEXT, filename_crc TEXT, filename_rom TEXT, filename_disknr INTEGER, scanned INTEGER, console TEXT, title TEXT, description BLOB, publisher TEXT, developer TEXT, year INTEGER, genre TEXT, players INTEGER, url_trailer TEXT, url_boxart TEXT, url_fanart TEXT, url_screen TEXT, url_banner TEXT, favorite INTEGER region TEXT, CONSTRAINT unique_game UNIQUE (filename, filename_archive, console));
